import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Calendar, Clock, Video, CreditCard, Shield, X } from "lucide-react";

interface Doctor {
  id: string;
  name: string;
  specialty: string;
  consultationFee: number;
  photo?: string;
}

interface BookingModalProps {
  isOpen: boolean;
  onClose: () => void;
  doctor: Doctor | null;
  onConfirmBooking?: (bookingData: any) => void;
}

export default function BookingModal({ isOpen, onClose, doctor, onConfirmBooking }: BookingModalProps) {
  const [step, setStep] = useState(1);
  const [bookingData, setBookingData] = useState({
    date: "",
    time: "",
    symptoms: "",
    patientName: "",
    patientAge: "",
    patientPhone: "",
    patientEmail: "",
    consultationType: "video"
  });

  const handleNext = () => {
    if (step < 3) {
      setStep(step + 1);
      console.log('Moved to step:', step + 1);
    }
  };

  const handleBack = () => {
    if (step > 1) {
      setStep(step - 1);
      console.log('Moved to step:', step - 1);
    }
  };

  const handleConfirmBooking = () => {
    onConfirmBooking?.(bookingData);
    console.log('Booking confirmed:', bookingData);
    onClose();
    setStep(1);
  };

  const updateBookingData = (field: string, value: string) => {
    setBookingData(prev => ({ ...prev, [field]: value }));
  };

  if (!doctor) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-3">
            <Avatar className="h-12 w-12">
              <AvatarImage src={doctor.photo} alt={doctor.name} />
              <AvatarFallback>
                {doctor.name.split(' ').map(n => n[0]).join('')}
              </AvatarFallback>
            </Avatar>
            <div>
              <div>Book Consultation</div>
              <div className="text-sm font-normal text-muted-foreground">
                Dr. {doctor.name} - {doctor.specialty}
              </div>
            </div>
          </DialogTitle>

          {/* Progress Indicator */}
          <div className="flex items-center gap-2 mt-4">
            {[1, 2, 3].map((stepNum) => (
              <div key={stepNum} className="flex items-center">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-xs font-medium ${
                  step >= stepNum ? 'bg-primary text-primary-foreground' : 'bg-muted text-muted-foreground'
                }`}>
                  {stepNum}
                </div>
                {stepNum < 3 && <div className="w-8 h-0.5 bg-muted mx-1" />}
              </div>
            ))}
          </div>
        </DialogHeader>

        {/* Step 1: Date & Time */}
        {step === 1 && (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="date">Preferred Date</Label>
                <Input
                  id="date"
                  type="date"
                  value={bookingData.date}
                  onChange={(e) => updateBookingData('date', e.target.value)}
                  data-testid="input-booking-date"
                />
              </div>
              <div>
                <Label htmlFor="time">Preferred Time</Label>
                <Select value={bookingData.time} onValueChange={(value) => updateBookingData('time', value)}>
                  <SelectTrigger data-testid="select-booking-time">
                    <SelectValue placeholder="Select time" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="09:00">09:00 AM</SelectItem>
                    <SelectItem value="10:00">10:00 AM</SelectItem>
                    <SelectItem value="11:00">11:00 AM</SelectItem>
                    <SelectItem value="14:00">02:00 PM</SelectItem>
                    <SelectItem value="15:00">03:00 PM</SelectItem>
                    <SelectItem value="16:00">04:00 PM</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="consultation-type">Consultation Type</Label>
              <Select value={bookingData.consultationType} onValueChange={(value) => updateBookingData('consultationType', value)}>
                <SelectTrigger data-testid="select-consultation-type">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="video">
                    <div className="flex items-center gap-2">
                      <Video className="h-4 w-4" />
                      Video Call
                    </div>
                  </SelectItem>
                  <SelectItem value="audio">
                    <div className="flex items-center gap-2">
                      <Clock className="h-4 w-4" />
                      Audio Call
                    </div>
                  </SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="symptoms">Symptoms or Reason for Visit</Label>
              <Textarea
                id="symptoms"
                placeholder="Briefly describe your symptoms or reason for consultation..."
                value={bookingData.symptoms}
                onChange={(e) => updateBookingData('symptoms', e.target.value)}
                data-testid="textarea-symptoms"
              />
            </div>
          </div>
        )}

        {/* Step 2: Patient Details */}
        {step === 2 && (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="patient-name">Patient Name</Label>
                <Input
                  id="patient-name"
                  value={bookingData.patientName}
                  onChange={(e) => updateBookingData('patientName', e.target.value)}
                  placeholder="Full name"
                  data-testid="input-patient-name"
                />
              </div>
              <div>
                <Label htmlFor="patient-age">Age</Label>
                <Input
                  id="patient-age"
                  type="number"
                  value={bookingData.patientAge}
                  onChange={(e) => updateBookingData('patientAge', e.target.value)}
                  placeholder="Age"
                  data-testid="input-patient-age"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="patient-phone">Phone Number</Label>
              <Input
                id="patient-phone"
                type="tel"
                value={bookingData.patientPhone}
                onChange={(e) => updateBookingData('patientPhone', e.target.value)}
                placeholder="+91 XXXXX XXXXX"
                data-testid="input-patient-phone"
              />
            </div>

            <div>
              <Label htmlFor="patient-email">Email Address</Label>
              <Input
                id="patient-email"
                type="email"
                value={bookingData.patientEmail}
                onChange={(e) => updateBookingData('patientEmail', e.target.value)}
                placeholder="your.email@example.com"
                data-testid="input-patient-email"
              />
            </div>
          </div>
        )}

        {/* Step 3: Payment Summary */}
        {step === 3 && (
          <div className="space-y-4">
            <div className="bg-muted/50 p-4 rounded-lg space-y-3">
              <h3 className="font-medium">Booking Summary</h3>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span>Consultation Fee</span>
                  <span>â‚¹{doctor.consultationFee}</span>
                </div>
                <div className="flex justify-between">
                  <span>Platform Fee</span>
                  <span>â‚¹50</span>
                </div>
                <div className="flex justify-between">
                  <span>GST (18%)</span>
                  <span>â‚¹{Math.round((doctor.consultationFee + 50) * 0.18)}</span>
                </div>
                <hr />
                <div className="flex justify-between font-medium">
                  <span>Total Amount</span>
                  <span>â‚¹{Math.round((doctor.consultationFee + 50) * 1.18)}</span>
                </div>
              </div>
            </div>

            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              <Shield className="h-4 w-4" />
              <span>Your payment is secure and encrypted</span>
            </div>

            <div className="bg-green-50 p-3 rounded-lg border border-green-200">
              <div className="text-sm font-medium text-green-800">
                100% Refund Policy
              </div>
              <div className="text-xs text-green-600">
                Full refund if cancelled 2+ hours before appointment
              </div>
            </div>
          </div>
        )}

        <DialogFooter className="flex gap-2">
          {step > 1 && (
            <Button variant="outline" onClick={handleBack} data-testid="button-back">
              Back
            </Button>
          )}
          <Button variant="outline" onClick={onClose} data-testid="button-cancel">
            Cancel
          </Button>
          {step < 3 ? (
            <Button onClick={handleNext} data-testid="button-next">
              Next
            </Button>
          ) : (
            <Button onClick={handleConfirmBooking} data-testid="button-confirm-booking">
              <CreditCard className="h-4 w-4 mr-2" />
              Pay & Book
            </Button>
          )}
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/BudgetTierCard.tsx.content:
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Check, Star, ArrowRight } from "lucide-react";

interface BudgetTier {
  id: string;
  title: string;
  price: string;
  description: string;
  features: string[];
  popular?: boolean;
  icon: React.ReactNode;
}

interface BudgetTierCardProps {
  tier: BudgetTier;
  onSelect?: (tier: BudgetTier) => void;
}

export default function BudgetTierCard({ tier, onSelect }: BudgetTierCardProps) {
  const handleSelect = () => {
    onSelect?.(tier);
    console.log('Budget tier selected:', tier.title);
  };

  return (
    <Card className={`relative hover-elevate transition-all duration-200 ${
      tier.popular ? 'ring-2 ring-primary' : ''
    }`}>
      {tier.popular && (
        <Badge className="absolute -top-3 left-1/2 transform -translate-x-1/2 bg-primary">
          <Star className="h-3 w-3 mr-1" />
          Most Popular
        </Badge>
      )}

      <CardHeader className="text-center pb-4">
        <div className="flex justify-center mb-3">
          <div className="p-3 rounded-lg bg-primary/10 text-primary">
            {tier.icon}
          </div>
        </div>
        <h3 className="text-xl font-semibold">{tier.title}</h3>
        <div className="text-3xl font-bold text-primary">{tier.price}</div>
        <p className="text-muted-foreground text-sm">{tier.description}</p>
      </CardHeader>

      <CardContent className="space-y-3">
        {tier.features.map((feature, index) => (
          <div key={index} className="flex items-start gap-2">
            <Check className="h-4 w-4 text-primary mt-0.5 flex-shrink-0" />
            <span className="text-sm">{feature}</span>
          </div>
        ))}
      </CardContent>

      <CardFooter>
        <Button 
          onClick={handleSelect}
          className="w-full group"
          variant={tier.popular ? "default" : "outline"}
          data-testid={`button-select-${tier.id}`}
        >
          Choose {tier.title}
          <ArrowRight className="h-4 w-4 ml-2 group-hover:translate-x-1 transition-transform" />
        </Button>
      </CardFooter>
    </Card>
  );
}

