      'basic': 500,
      'specialist': 1000,
      'premium': 2000
    };
    setSearchParams({
      maxFee: feeMapping[tier.id] || undefined
    });
    console.log('Budget tier selected:', tier.title);
  };

  return (
    <div className="min-h-screen bg-background">
